from flask import Blueprint, request, render_template, session, send_file, Response
import base64

app = Blueprint('pathtraversal',__name__)

# Base64 encoded sample files
SAMPLE_PDF_BASE64 = ""
PASSWD_FILE_BASE64 = ""

@app.route('/pathtraversal')
def pagePathtraversal():
    return render_template('inside.html', admin=False, page='pathtraversal', title='Path Traversal Demo', message='<p>Click on <a href="/pathtraversal/download?file=usage_report.pdf">usage report</a> to download the latest usage statistics.</p>')

@app.route('/pathtraversal/download')
def download_file():
    # Get the requested file parameter
    requested_file = request.args.get('file', '')
    
    # Vulnerable implementation - directly uses the user input for path traversal demonstration
    
    # For demo purposes, simulate /etc/passwd access with a hardcoded base64 encoded file
    if '/etc/passwd' in requested_file:
        passwd_content = base64.b64decode(PASSWD_FILE_BASE64)
        return Response(
            passwd_content,
            mimetype="text/plain",
            headers={"Content-Disposition": "attachment;filename=passwd"}
        )
    
    # Return sample PDF file for the legitimate request
    if requested_file == 'usage_report.pdf':
        pdf_content = base64.b64decode(SAMPLE_PDF_BASE64)
        return Response(
            pdf_content,
            mimetype="application/pdf",
            headers={"Content-Disposition": "attachment;filename=usage_report.pdf"}
        )
       
    # Default case - file not found
    return "File not found", 404